mapper.select:
select 

from ad_tree

	where addr_level >= #{addrLevel,jdbcType=INTEGER} 


	where addr_level > = #{addrLevel,jdbcType=INTEGER}


 	and addr_private_name like '${baseScope}%' 


 	and addr_full_name like '%${keyword}%'



 	and status = #{status,jdbcType=VARCHAR}

 order by addr_level

select t.addr_id,t.addr_name,t.addr_level,t.addr_type,t.addr_use,t.is_blank,t.addr_parent,t.addr_private_name, t.addr_full_name,t.addr_code,t.county_id,t.create_time,t.create_optr_id,t.create_done_code, t.status,t.str1,t.str2,t.str3,t.str4,t.str5 from ad_tree t,ad_collections c where c.addr_id = t.addr_id and c.userid = #{userid,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
order by addr_level

select 

from ad_tree 


 	where addr_parent = #{addrParent,jdbcType=INTEGER}


 	and status = #{status,jdbcType=VARCHAR}


 	and addr_private_name like '${baseScope}%' 

 order by addr_level
